const useStyles = makeStyles((theme) => ({
  root: {
    background: theme.background,
    border: 0,
    fontSize: 16,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height: 48,
    padding: '0 30px',
  },
}));

const theme = createTheme({
  palette:{
    primary:{
      main:green[400],
    },
    secondary:{
      main:orange[400],
    }
  }
})

function ButtonStyled(){
  const classes = useStyles()
  return <Button variant="contained" className={classes.root}>Test Button Style</Button>
}
const MyButton = styled(Button)({
  background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
  border: 0,
  borderRadius: 3,
  boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
  color: 'white',
  height: 48,
  padding: '0 30px',
});


function StyledComponents() {
  return <MyButton variant="contained">Styled Components</MyButton>;
}
// Nesting selectors
function NestedStylesHook() {
  const classes = useStyles();

  return (
    <div className={classes.nestedCss}>
      This is red since it is inside the root.
      <p>
        This is green since it is inside the paragraph{' '}
        <span>and this is blue since it is inside the span</span>
      </p>
    </div>
  );
}
// Adapting based on props
function MiButton(props) {
  const { color, ...other } = props;
  const classes = useStyles(props);
  return <Button className={classes.btns} {...other} />;
}
function AdaptingHook() {
  return (
    <React.Fragment>
      <MiButton variant="contained" color="red">Red</MiButton>
      <MiButton variant="contained" color="blue">Blue</MiButton>
    </React.Fragment>
  );
}
MiButton.propTypes = {
  color: PropTypes.oneOf(['blue', 'red']).isRequired,
};

// theming
function DeepChild() {
  // const classes = useStyles();
  // return (
  //   <button type="button" className={classes.root}>
  //     Theming
  //   </button>
  // );
  const theme = useTheme();
  return <span>{`spacing ${theme.spacing}`}</span>;
}
const themeInstance = {
  background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
};
function UseTheme() {
  return (
    <ThemeProvider
      theme={{
        spacing: '8px',
      }}
    >
      <DeepChild />
    </ThemeProvider>
  );
}

