import React, { useState } from "react";

export const UseStateBasics = () => {
  const [state, setState] = useState("");

  let title = "Random title";
  const handleTitle = () => {
    title = "Hello people";
    console.log(title);
    setState(title);
  };
  return (
    <div>
      <h3>{title}</h3>
      <button type="button" onClick={() => setState(!state)}>{`${
        state ? "Change title" : "Hello People"
      }`}</button>
    </div>
  );
};
